import React, { useState, useEffect } from "react";
import { useInView } from "react-intersection-observer";
import "../styles/Projects.css";

const Projects = () => {
  const { ref, inView } = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const projects = [
    {
      id: 1,
      title: "Online Perfume Store",
      description:
        "A complete e-commerce website for perfumes with product catalog, shopping cart, user authentication, and seamless checkout experience. Features include product filtering, search functionality, and order management.",
      technologies: ["React", "Node.js", "MongoDB", "Express", "JWT"],
      image: "/images/project1.jpg",
      github: "https://github.com/username/perfume-store",
      live: "https://perfume-store-demo.netlify.app",
      featured: true,
      category: "Full Stack",
    },
    {
      id: 2,
      title: "Kanban To-Do List Website",
      description:
        "An interactive task management application with Kanban board layout. Features drag-and-drop functionality to organize tasks across different stages (To Do, In Progress, Done), task creation, editing, deletion, and local storage persistence.",
      technologies: ["React", "CSS3", "Local Storage", "React DnD"],
      image: "/images/project2.jpg",
      github: "https://github.com/username/kanban-todo",
      live: "https://kanban-todo-demo.netlify.app",
      featured: true,
      category: "Frontend",
    },
    {
      id: 3,
      title: "Live Dictionary Website",
      description:
        "A real-time dictionary application with instant word search, pronunciation, definitions, synonyms, antonyms, and example sentences. Features include search history, favorites, and responsive design for all devices.",
      technologies: ["React", "Dictionary API", "JavaScript", "CSS3"],
      image: "/images/project3.jpg",
      github: "https://github.com/username/live-dictionary",
      live: "https://live-dictionary-demo.netlify.app",
      featured: false,
      category: "Frontend",
    },
  ];

  return (
    <section id="projects" className="projects-section">
      <div className="container">
        <h2 className="section-title">My Projects</h2>
        <div
          ref={ref}
          className={`projects-grid ${inView ? "fade-in" : ""}`}
        >
          {projects.map((project) => (
            <div key={project.id} className="project-card">
              <div className="project-image">
                <div className="image-placeholder">{project.title}</div>
              </div>
              <div className="project-content">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <div className="technologies">
                  {project.technologies.map((tech) => (
                    <span key={tech} className="tech-tag">
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
